jQuery(document).ready(function(){

	 jQuery(".sortable" ).sortable({
			items: "tr.sortrow",
			handle: ".dragger",
			axis: "y",
			placeholder: "ui-state-highlight",
			connectWith: ".sortable",
			update: function(event, ui) {
				var newOrder = jQuery(this).sortable('toArray' , {attribute: 'data-row' }).toString();
				var group = jQuery(this).data('group');
				console.log(newOrder);
				jQuery.ajax({
					url: '/admin/update_order',
					type: "POST",
					data: {"order": newOrder, "group" : group},
					success : function (data) {

						console.log(data);



	         		 jQuery('#notification').show().html('<div class=" fade in alert alert-success  alert-dismissable">Order updated successfully!</div>').fadeOut(3000);
					}
				}); // end of ajax

			}

	   });
}); 