jQuery(document).ready(function(){



	if(jQuery().fancybox) {

		jQuery(".fancybox").fancybox({
			openEffect	: 'none',
			closeEffect	: 'none',
			helpers		: {
				title	: { type : 'inside' },
				buttons	: {}
			}
		});

	 }

	if(jQuery().unslider) {
		jQuery('.chilly_banner').each(function(){

				var $banner = jQuery(this);
				var unslider = $banner.unslider(Slideroptions);
			    $banner.find('.unslider-arrow').click(function(e) {
			    	e.preventDefault();
			        var fn = this.className.split(' ')[1];
			        //  Either do unslider.data('unslider').next() or .prev() depending on the className
			        unslider.data('unslider')[fn]();
			    });
		});
	};

	if(jQuery().masonry) {


		var $container = jQuery('#chilly_masonry');
		// initialize
		$container.masonry({
			 columnWidth: 5,
		  itemSelector: '.chilly_item'
		});
		
	}





});