jQuery(document).ready(function($) {

    /***** Colour picker *****/

    $('.colorpicker').hide();
    $('.colorpicker').each( function() {
        $(this).farbtastic( $(this).closest('.color-picker').find('.color') );
    });

    $('.color').click(function() {
        $(this).closest('.color-picker').find('.colorpicker').fadeIn();
    });

    $(document).mousedown(function() {
        $('.colorpicker').each(function() {
            var display = $(this).css('display');
            if ( display == 'block' )
                $(this).fadeOut();
        });
    });


    /***** Uploading images *****/

    var file_frame;
    var hidden_info_block = '';
    var img_count = 0;

    jQuery.fn.uploadMediaFile = function( button, preview_media ) {
        var button_id = button.attr('id');
        var field_id = button_id.replace( '_button', '' );
        var preview_id = button_id.replace( '_button', '_preview' );

        // If the media frame already exists, reopen it.
        if ( file_frame ) {
          file_frame.open();
          return;
        }

        // Create the media frame.
        file_frame = wp.media.frames.file_frame = wp.media({
          title: jQuery( this ).data( 'uploader_title' ),
          button: {
            text: jQuery( this ).data( 'uploader_button_text' ),
          },
          multiple: true
        });

        // When an image is selected, run a callback.
        file_frame.on( 'select', function() {
        	   var selection = file_frame.state().get('selection');
       
		  selection.map( function( attachment ) {
                attachment = attachment.toJSON();
                hidden_info_block = hidden_info_block + "<input class='chilly_gallery_img_info' type='hidden' name='cgallery_img_id_row_"+img_count+"' value='" + attachment.id + "'/>";
                img_count = img_count + 1;
            });
            if ((img_count > 0) ){
                //if there are images specified let's display a <tr> row before the images stating how many were selected
                var image_count_notice = jQuery('#image_count_notice');
                image_count_notice.html( img_count+' images were selected');
                image_count_notice.after(hidden_info_block);
            }

          file_frame = false;
        });

        // Finally, open the modal
        file_frame.open();
    }

    jQuery('.image_upload_button').click(function() {
        jQuery.fn.uploadMediaFile( jQuery(this), true );
    });

    jQuery('.image_delete_button').click(function() {
        jQuery(this).closest('td').find( '.image_data_field' ).val( '' );
        jQuery(this).closest('td').find( '.image_preview' ).remove();
        return false;
    });

});